import './SearchBooksAdmin.css';
import {Link} from "react-router-dom";
import { useEffect,useState } from 'react';
import {numberValidataion,alphanumericFiftyValidation,alphanumericOnefiftyValidation} from '../Validations';
import axios from 'axios';
import {Component} from 'react';

class SearchBooksAdmin extends Component{
  constructor(props){
    super(props);
    this.state={
      list:[]
    }
  }

  componentDidMount(){
    axios.get('http://localhost:3000/bookDetails').then((response)=>{
      this.setState({
        list:response.data
      })
    }).catch((error)=>{
      console.log(error);
    });
  }

  onDeleteHandler=(index)=>{
        let bookDetails = this.state.list;
        let id=String(index);
        axios.delete('http://localhost:3000/bookDetails/'+id).then(()=>{
          axios.get('http://localhost:3000/bookDetails').then((response)=>{
          this.setState({
          list:response.data
          })
          }).catch((error)=>{
            console.log(error);
          });
        }).catch(()=>{

        });
  }

    onEditHandler=(index)=>{
      let bookDetails = this.state.list;
      let idindex=1;
      this.setState={
        id:index,
        bookTitle:bookDetails[idindex].bookTitle,
        bookDesc:bookDetails[idindex].bookDesc,
        author:bookDetails[index].author,
        noOfBooks:bookDetails[index].noOfBooks
      }
    }

    onChangeHandler=(event)=>{
          this.setState({
            [event.target.name]:event.target.value
          })
        }

  render(){
    return(<div>
      <div className="container-fluid">
              <div className="row">
                  <div className="col-12 heading">
                      Search Book
                  </div>
              </div>
              {/* <div className="row">
                <div className="col-3">
                  <form id="searchBook"> 
                      <div className="form-group">
                        <label htmlFor="bookTitle">Book Title</label>
                        <input type="text" value={getSearch} onChange={onChangeSearchHandler} className="form-control" id="bookTitle"/>
                      </div>

                      <button id="search" onClick={searchFilter} type="submit" className="btn btn-primary">Search</button>
                      <button id="reset" onClick={resetFilter} className="btn btn-primary">Reset</button>

                    </form>
              </div>
              <div className="col-9"></div>
              </div> */}

              <div className="row">
                  <div className="col-12">
                    <table className="table">
                        <thead>
                          <tr>
                            <th scope="col">Book ID</th>
                            <th scope="col">Book Name</th>
                            <th scope="col">Book Description</th>
                            <th scope="col">Author</th>
                            <th scope="col">Number of books available</th>
                            <th scope="col">Edit</th>
                            <th scope="col">Delete</th>
                          </tr>
                        </thead>
                        <tbody>
                        {this.state.list.map((obj)=>{
                           return(<tr key={obj.id}>
                            <td>{obj.id}</td>
                            <td>{obj.bookTitle}</td>
                            <td>{obj.bookDesc}</td>
                            <td>{obj.author}</td>
                            <td>{obj.noOfBooks}</td>
                            <td><i  data-toggle="modal" onClick={()=>this.onEditHandler(obj.id)} data-target="#edit" className="fa fa-pencil-square-o" aria-hidden="true"></i></td>
                            <td><i className="fa fa-trash" onClick={()=>this.onDeleteHandler(obj.id)} aria-hidden="true"></i></td>
                          </tr>

                           )
                        })

                        }
                        </tbody>
                      </table>
                  </div>
              </div>

          </div>


  <div className="modal fade" id="edit" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div className="modal-dialog" role="document">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
          <button type="button" className="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="modal-body">
        <form id="addBook"> 
            <div className="form-group">
              <label name="bookId">Book ID</label>
              <input type="text" value={this.id} onChange={this.onChangeHandler} className="form-control" id="bookId" name="bookId" placeholder="Book ID"/>
              {/* {getValidation.bookId && <div style={{fontSize:"10px"}} class="alert alert-danger" role="alert">
              {getValidation.bookId}
              </div>} */}
            </div>
            <div className="form-group">
              <label name="bookTitle">Book Title</label>
              <input type="text" value={this.bookTitle} onChange={this.onChangeHandler} className="form-control" id="bookTitle" name="bookTitle" placeholder="Book Title"/>
              {/* {getValidation.bookTitle && <div style={{fontSize:"10px"}} className="alert alert-danger" role="alert">
              {getValidation.bookTitle}
              </div>} */}
            </div>
            <div className="form-group">
              <label name="bookDesc">Book Description</label>
              <input type="text" value={this.bookDesc} onChange={this.onChangeHandler} className="form-control" id="bookDesc" name="bookDesc"  placeholder="Book Description"/>
              {/* {getValidation.bookDesc && <div style={{fontSize:"10px"}} className="alert alert-danger" role="alert">
              {getValidation.bookDesc}
              </div>} */}
            </div>
            <div className="form-group">
              <label name="author">Author Name</label>
              <input type="text" value={this.author} onChange={this.onChangeHandler} className="form-control" id="author" name="author"  placeholder="Author Name"/>
              {/* {getValidation.author && <div style={{fontSize:"10px"}} className="alert alert-danger" role="alert">
              {getValidation.author}
              </div>} */}
            </div>
            <div className="form-group">
              <label name="noOfBooks">Number of Books Available</label>
              <input type="text" value={this.noOfBooks} onChange={this.onChangeHandler} className="form-control" id="noOfBooks" name="noOfBooks"  placeholder="Number of books available"/>
              {/* {getValidation.noOfBooks && <div style={{fontSize:"10px"}} className="alert alert-danger" role="alert">
              {getValidation.noOfBooks}
              </div>} */}
            </div>
            </form>
        </div>
        <div className="modal-footer">
          {/* <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
          <button type="button" data-dismiss="modal" onClick={onEditSubmitHandler} className="btn btn-primary">Save changes</button> */}
        </div>
      </div>
    </div>
  </div>
  </div>)
  }
}


